/**
 * Run Viewer (autopilot capture UI)
 *
 * Business intent:
 * - Provide a zero-deps, no-build-step viewer for autopilot run captures under runs/autopilot/.
 * - Serve a tiny static HTML UI plus read-only JSON/file streaming APIs to inspect runs locally.
 *
 * Gotchas:
 * - This serves local files from the run folder; keep it bound to localhost when possible.
 * - Transcript lookup is best-effort and depends on Codex home/session layout.
 */
import { createReadStream } from "node:fs";
import { readdir, readFile, stat } from "node:fs/promises";
import http from "node:http";
import os from "node:os";
import path from "node:path";
import process from "node:process";
import { URL } from "node:url";

type ExecArtifactPaths = {
  eventsJsonl?: string;
  stderrTxt?: string;
  lastMessageTxt?: string;
  promptTxt?: string;
  argvJson?: string;
  schemaJson?: string;
};

type ExecManifestEntry = {
  execId: string;
  label: string;
  threadId: string;
  status: "succeeded" | "failed";
  exitCode: number;
  startedAt: string;
  finishedAt: string;
  artifacts: ExecArtifactPaths;
};

type GraphNode =
  | {
      id: string;
      type: "exec";
      execId: string;
      label: string;
      threadId: string;
      artifacts: ExecArtifactPaths;
    }
  | {
      id: string;
      type: "thread";
      threadId: string;
    };

type GraphEdge = {
  type: "dependsOn" | "invokes" | "resume" | "spawn" | "interact";
  from: string;
  to: string;
  callId?: string;
  status?: string;
  prompt?: string;
  source?: "workflow" | "resume" | "transcript";
};

type RunManifest = {
  runId: string;
  startedAt: string;
  finishedAt?: string;
  cwd: string;
  options?: Record<string, unknown>;
  execs: ExecManifestEntry[];
  graph?: {
    nodes: GraphNode[];
    edges: GraphEdge[];
    warnings?: string[];
  };
};

type Args = {
  port: number;
  host: string;
  codexHome: string;
};

const DEFAULT_PORT = 4141;
const DEFAULT_HOST = "127.0.0.1";

const args = parseArgs(process.argv.slice(2));
if (!args) {
  printHelp();
  process.exit(1);
}

const server = http.createServer(async (req, res) => {
  try {
    await routeRequest(req, res, args.codexHome);
  } catch (error) {
    const message = error instanceof Error ? error.message : String(error);
    res.writeHead(500, { "content-type": "application/json; charset=utf-8" });
    res.end(JSON.stringify({ error: "internal_error", message }));
  }
});

server.listen(args.port, args.host, () => {
  const address = `http://${args.host}:${args.port}`;
  // eslint-disable-next-line no-console
  console.log(`Run viewer listening on ${address}`);
});

async function routeRequest(
  req: http.IncomingMessage,
  res: http.ServerResponse,
  codexHome: string,
): Promise<void> {
  if (!req.url) {
    res.writeHead(400);
    res.end("Bad request");
    return;
  }
  const url = new URL(req.url, `http://${req.headers.host ?? "localhost"}`);
  const pathname = url.pathname;

  if (pathname === "/" || pathname === "/index.html") {
    res.writeHead(200, { "content-type": "text/html; charset=utf-8" });
    res.end(renderHtml());
    return;
  }

  if (pathname === "/api/runs") {
    await handleRunsList(res);
    return;
  }

  if (pathname.startsWith("/api/runs/") && pathname.endsWith("/manifest")) {
    const runId = pathname.split("/")[3];
    if (!runId) {
      res.writeHead(400, { "content-type": "application/json; charset=utf-8" });
      res.end(JSON.stringify({ error: "missing_run_id" }));
      return;
    }
    await handleManifest(res, runId);
    return;
  }

  if (pathname.startsWith("/api/runs/") && pathname.endsWith("/file")) {
    const runId = pathname.split("/")[3];
    if (!runId) {
      res.writeHead(400, { "content-type": "application/json; charset=utf-8" });
      res.end(JSON.stringify({ error: "missing_run_id" }));
      return;
    }
    const relPath = url.searchParams.get("path");
    if (!relPath) {
      res.writeHead(400, { "content-type": "application/json; charset=utf-8" });
      res.end(JSON.stringify({ error: "missing_path" }));
      return;
    }
    await handleRunFile(res, runId, relPath);
    return;
  }

  if (pathname.startsWith("/api/transcript/")) {
    const threadId = pathname.split("/")[3];
    if (!threadId) {
      res.writeHead(400, { "content-type": "application/json; charset=utf-8" });
      res.end(JSON.stringify({ error: "missing_thread_id" }));
      return;
    }
    const metaOnly = url.searchParams.get("meta") === "1";
    await handleTranscript(res, threadId, codexHome, metaOnly);
    return;
  }

  res.writeHead(404, { "content-type": "text/plain; charset=utf-8" });
  res.end("Not found");
}

function parseArgs(argv: string[]): Args | null {
  let port = DEFAULT_PORT;
  let host = DEFAULT_HOST;
  let codexHome = path.join(os.homedir(), ".codex");

  for (let i = 0; i < argv.length; i += 1) {
    const arg = argv[i];
    if (arg === "--help" || arg === "-h") {
      return null;
    }
    if (arg === "--port") {
      const next = argv[i + 1];
      if (!next) return null;
      port = Number(next);
      i += 1;
      continue;
    }
    if (arg === "--host") {
      const next = argv[i + 1];
      if (!next) return null;
      host = next;
      i += 1;
      continue;
    }
    if (arg === "--codex-home") {
      const next = argv[i + 1];
      if (!next) return null;
      codexHome = next;
      i += 1;
      continue;
    }
    return null;
  }

  if (!Number.isFinite(port) || port <= 0) return null;
  return { port, host, codexHome };
}

function printHelp(): void {
  // eslint-disable-next-line no-console
  console.log(`Usage: node examples/run-viewer.ts [--port <port>] [--host <host>] [--codex-home <path>]`);
}

