745	      async function streamEvents() {
746	        const logEl = document.getElementById("events-log");
747	        if (!logEl || !state.exec?.artifacts?.eventsJsonl) return;
748	        logEl.textContent = "";
749	        const res = await fetch(\`/api/runs/\${state.run.runId}/file?path=\${encodeURIComponent(state.exec.artifacts.eventsJsonl)}\`);
750	        if (!res.ok || !res.body) {
751	          logEl.textContent = "Failed to stream events.";
752	          return;
753	        }
754	        const reader = res.body.getReader();
755	        const decoder = new TextDecoder();
756	        let buffer = "";
757	        let shown = 0;
758	        while (true) {
759	          const { value, done } = await reader.read();
760	          if (done) break;
761	          buffer += decoder.decode(value, { stream: true });
762	          let index;
763	          while ((index = buffer.indexOf("\n")) >= 0) {
764	            const line = buffer.slice(0, index).trim();
765	            buffer = buffer.slice(index + 1);
766	            if (!line) continue;
767	            const formatted = filterEventLine(line, state.eventsFilter);
768	            if (formatted) {
769	              logEl.textContent += formatted + "\n";
770	              shown += 1;
771	            }
772	          }
773	        }
774	        if (buffer.trim()) {
775	          const formatted = filterEventLine(buffer.trim(), state.eventsFilter);
776	          if (formatted) {
777	            logEl.textContent += formatted + "\n";
778	            shown += 1;
779	          }
780	        }
781	        if (shown === 0) {
782	          logEl.textContent = "No events matched the filter.";
783	        }
784	      }
785	
786	      function filterEventLine(line, filters) {
787	        let json;
788	        try {
789	          json = JSON.parse(line);
790	        } catch {
791	          return null;
792	        }
793	        const type = json.type || json.event?.type || json.payload?.type || "";
794	        if (filters.type && !String(type).includes(filters.type)) return null;
795	        if (filters.text && !line.includes(filters.text)) return null;
796	        return JSON.stringify(json, null, 2);
797	      }
