function renderExecs() {
  execsEl.innerHTML = "";
  const title = document.createElement("h2");
  title.textContent = "Execs";
  execsEl.appendChild(title);
  if (!state.run) {
    const empty = document.createElement("div");
    empty.className = "empty";
    empty.textContent = "Select a run.";
    execsEl.appendChild(empty);
    return;
  }
  state.run.execs.forEach((exec) => {
    const card = document.createElement("div");
    card.className = "item" + (state.exec && state.exec.execId === exec.execId ? " active" : "");
    const titleEl = document.createElement("div");
    titleEl.className = "title";
    titleEl.textContent = exec.execId;
    const subtitleEl = document.createElement("div");
    subtitleEl.className = "subtitle";
    subtitleEl.textContent = \`\${exec.label} • \${exec.status}\`;
    card.append(titleEl, subtitleEl);
    card.onclick = () => selectExec(exec.execId);
    execsEl.appendChild(card);
  });
}

async function selectRun(runId) {
  const res = await fetch(\`/api/runs/\${runId}/manifest\`);
  if (!res.ok) {
    alert("Failed to load manifest.");
    return;
  }
  state.run = await res.json();
  state.exec = state.run.execs[0] || null;
  state.transcriptPath = null;
  abortEventsStream();
  renderExecs();
  renderPanel();
}

function selectExec(execId) {
  state.exec = state.run.execs.find((exec) => exec.execId === execId) || null;
  state.transcriptPath = null;
  abortEventsStream();
  renderExecs();
  renderPanel();
}

function renderPanel() {
  renderTabs();
  if (state.tab !== "Events") {
    abortEventsStream();
  }
  if (!state.run) {
    panelEl.innerHTML = \`<div class="empty">Select a run to inspect.</div>\`;
    return;
  }
  switch (state.tab) {
    case "Overview":
      renderOverview();
      break;
    case "Graph":
      renderGraph();
      break;
    case "Output":
      renderFilePanel("Output", state.exec?.artifacts?.lastMessageTxt);
      break;
    case "Prompt":
      renderFilePanel("Prompt", state.exec?.artifacts?.promptTxt);
      break;
    case "Events":
      renderEvents();
      break;
    case "Stderr":
      renderFilePanel("Stderr", state.exec?.artifacts?.stderrTxt);
      break;
    case "Transcript":
      renderTranscript();
      break;
  }
}

function renderOverview() {
  if (!state.exec) {
    panelEl.innerHTML = \`<div class="empty">Select an exec.</div>\`;
    return;
  }
  const exec = state.exec;
  panelEl.innerHTML = "";
  const title = document.createElement("h3");
  title.textContent = "Run Overview";
  const chips = document.createElement("div");
  chips.className = "row";
  chips.append(
    makeChip(\`Run: \${state.run.runId}\`),
    makeChip(\`Exec: \${exec.execId}\`),
    makeChip(\`Thread: \${exec.threadId}\`),
    makeChip(\`Status: \${exec.status}\`),
  );
  const buttons = document.createElement("div");
  buttons.className = "buttons";
  const resumeBtn = document.createElement("button");
  resumeBtn.className = "primary";
  resumeBtn.id = "copy-resume";
  resumeBtn.textContent = "Copy resume command";
  resumeBtn.disabled = !(exec.threadId && exec.artifacts?.promptTxt);
  const transcriptBtn = document.createElement("button");
  transcriptBtn.id = "copy-transcript";
  transcriptBtn.textContent = "Copy open transcript path";
  transcriptBtn.disabled = !exec.threadId;
  buttons.append(resumeBtn, transcriptBtn);
  const detail = document.createElement("div");
  detail.append(
    makeStrongLine("Exec label:", exec.label),
    document.createElement("br"),
    makeStrongLine("Started:", exec.startedAt),
    document.createElement("br"),
    makeStrongLine("Finished:", exec.finishedAt),
    document.createElement("br"),
    makeStrongLine("Exit code:", String(exec.exitCode)),
  );
  panelEl.append(title, chips, buttons, detail);
  resumeBtn.addEventListener("click", async () => {
    const promptText = await loadText(exec.artifacts?.promptTxt);
    if (!promptText) return;
    const escaped = escapeShellDoubleQuotes(promptText.replace(/\n/g, " "));
    const cmd = \`codex exec resume \${exec.threadId} "\${escaped}"\`;
    await navigator.clipboard.writeText(cmd);
  });
  transcriptBtn.addEventListener("click", async () => {
    if (!exec.threadId) return;
    const meta = await fetch(\`/api/transcript/\${exec.threadId}?meta=1\`);
    if (!meta.ok) {
      alert("Transcript not found.");
      return;
    }
    const payload = await meta.json();
    if (payload.path) {
      await navigator.clipboard.writeText(payload.path);
    }
  });
}

function renderGraph() {
  const graph = state.run.graph;
  if (!graph) {
    panelEl.innerHTML = \`<div class="empty">No graph recorded.</div>\`;
    return;
  }
  panelEl.innerHTML = "";
  const title = document.createElement("h3");
  title.textContent = "Graph";
  panelEl.appendChild(title);
  if (graph.warnings?.length) {
    const warningsRow = document.createElement("div");
    warningsRow.className = "row";
    graph.warnings.forEach((warning) => warningsRow.appendChild(makeChip(warning)));
    panelEl.appendChild(warningsRow);
  }
  panelEl.appendChild(makeHeader("Nodes"));
  const nodesTable = document.createElement("table");
  nodesTable.appendChild(makeTableHeader(["Id", "Type", "Label", "Exec", "Thread", ""]));
  const nodesBody = document.createElement("tbody");
  if (!graph.nodes.length) {
    nodesBody.appendChild(makeEmptyRow(6, "No nodes"));
  } else {
    graph.nodes.forEach((node) => {
      const row = document.createElement("tr");
      const execId = node.type === "exec" ? node.execId : "-";
      const label = node.type === "exec" ? node.label : "thread";
      row.append(
        makeCell(node.id),
        makeCell(node.type),
        makeCell(label),
        makeCell(execId),
        makeCell(node.threadId || ""),
      );
      const actionCell = document.createElement("td");
      if (node.type === "exec" && execId) {
        const btn = document.createElement("button");
        btn.setAttribute("data-exec", execId);
        btn.textContent = "Open";
        btn.addEventListener("click", () => {
          selectExec(execId);
          state.tab = "Overview";
          renderPanel();
        });
        actionCell.appendChild(btn);
      }
      row.appendChild(actionCell);
      nodesBody.appendChild(row);
    });
  }
  nodesTable.appendChild(nodesBody);
  panelEl.appendChild(nodesTable);
  panelEl.appendChild(makeHeader("Edges"));
  const edgesTable = document.createElement("table");
  edgesTable.appendChild(makeTableHeader(["Type", "From", "To", "Source"]));
  const edgesBody = document.createElement("tbody");
  if (!graph.edges.length) {
    edgesBody.appendChild(makeEmptyRow(4, "No edges"));
  } else {
    graph.edges.forEach((edge) => {
      const row = document.createElement("tr");
      row.append(
        makeCell(edge.type),
        makeCell(edge.from),
        makeCell(edge.to),
        makeCell(edge.source || ""),
      );
      edgesBody.appendChild(row);
    });
  }
  edgesTable.appendChild(edgesBody);
  panelEl.appendChild(edgesTable);
}

async function renderFilePanel(label, relPath) {
  if (!state.exec || !relPath) {
    panelEl.innerHTML = \`<div class="empty">No \${label.toLowerCase()} captured.</div>\`;
    return;
  }
  const text = await loadText(relPath);
  if (text === null) {
    panelEl.innerHTML = \`<div class="empty">Unable to load \${label.toLowerCase()}.</div>\`;
    return;
  }
  panelEl.innerHTML = \`<h3>\${label}</h3><pre>\${escapeHtml(text)}</pre>\`;
}

function renderEvents() {
  if (!state.exec || !state.exec.artifacts?.eventsJsonl) {
    panelEl.innerHTML = \`<div class="empty">No events captured.</div>\`;
    return;
  }
  panelEl.innerHTML = \`
    <h3>Events</h3>
    <div class="filters">
      <input type="text" id="event-type" placeholder="Filter by event.type" />
      <input type="text" id="event-text" placeholder="Search text" />
      <button id="reload-events">Reload</button>
    </div>
    <pre id="events-log">Loading...</pre>
  \`;
  const typeInput = document.getElementById("event-type");
  const textInput = document.getElementById("event-text");
  const reloadButton = document.getElementById("reload-events");
  typeInput.value = state.eventsFilter.type;
  textInput.value = state.eventsFilter.text;
  reloadButton.onclick = () => {
    state.eventsFilter.type = typeInput.value.trim();
    state.eventsFilter.text = textInput.value.trim();
    streamEvents();
  };
  streamEvents();
}

function abortEventsStream() {
  if (state.eventsAbortController) {
    state.eventsAbortController.abort();
    state.eventsAbortController = null;
  }
}

async function streamEvents() {
  const logEl = document.getElementById("events-log");
  if (!logEl || !state.exec?.artifacts?.eventsJsonl) return;
  abortEventsStream();
  const controller = new AbortController();
  state.eventsAbortController = controller;
  logEl.textContent = "";
  let res;
  try {
    res = await fetch(
      \`/api/runs/\${state.run.runId}/file?path=\${encodeURIComponent(state.exec.artifacts.eventsJsonl)}\`,
      { signal: controller.signal },
    );
  } catch (error) {
    if (controller.signal.aborted) return;
    logEl.textContent = "Failed to stream events.";
    return;
  }
  if (!res.ok || !res.body) {
    if (!controller.signal.aborted) {
      logEl.textContent = "Failed to stream events.";
    }
    return;
  }
  const reader = res.body.getReader();
  const decoder = new TextDecoder();
  let buffer = "";
  let shown = 0;
  const appendChunk = (text) => {
    if (!text) return;
    if (state.eventsAbortController !== controller) return;
    logEl.appendChild(document.createTextNode(text));
  };
  try {
    while (true) {
      const { value, done } = await reader.read();
      if (done) break;
      buffer += decoder.decode(value, { stream: true });
      let index;
      let chunk = "";
      while ((index = buffer.indexOf("\n")) >= 0) {
        const line = buffer.slice(0, index).trim();
        buffer = buffer.slice(index + 1);
        if (!line) continue;
        const formatted = filterEventLine(line, state.eventsFilter);
        if (formatted) {
          chunk += formatted + "\n";
          shown += 1;
        }
      }
      appendChunk(chunk);
    }
  } catch (error) {
    if (!controller.signal.aborted) {
      logEl.textContent = "Failed to stream events.";
    }
    return;
  }
  if (buffer.trim()) {
    const formatted = filterEventLine(buffer.trim(), state.eventsFilter);
    if (formatted) {
      appendChunk(formatted + "\n");
      shown += 1;
    }
  }
  if (controller.signal.aborted) return;
  if (shown === 0) {
    logEl.textContent = "No events matched the filter.";
  }
}

function filterEventLine(line, filters) {
  let json;
  try {
    json = JSON.parse(line);
  } catch {
    return null;
  }
  const type = json.type || json.event?.type || json.payload?.type || "";
  if (filters.type && !String(type).includes(filters.type)) return null;
  if (filters.text && !line.includes(filters.text)) return null;
  return JSON.stringify(json, null, 2);
}

async function renderTranscript() {
  if (!state.exec?.threadId) {
    panelEl.innerHTML = \`<div class="empty">No thread id available.</div>\`;
