  return text
    .replace(/&/g, "&amp;")
    .replace(/</g, "&lt;")
    .replace(/>/g, "&gt;")
    .replace(/"/g, "&quot;")
    .replace(/'/g, "&#39;");
}

function escapeShellDoubleQuotes(text) {
  return text.replace(/[\\$"\u0060]/g, (match) => "\\" + match);
}

